import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const WeatherApp = () => {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');
  const [error, setError] = useState(null);
  const [bgClass, setBgClass] = useState('');

  // Function to fetch weather by coordinates
  const fetchWeatherByCoords = useCallback(async (lat, lon) => {
    try {
      const response = await axios.get('/api/weather/', { params: { lat, lon } });
      setWeather(response.data);
      updateBg(response.data.weather[0]?.main);
      setError(null);
    } catch (err) {
      setError('Error fetching weather data.');
    }
  }, []);

  // Fetch location-based weather on initial load
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        ({ coords }) => fetchWeatherByCoords(coords.latitude, coords.longitude),
        () => setError('Unable to retrieve your location.')
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, [fetchWeatherByCoords]);

  // Function to fetch weather by city name
  const fetchWeatherByCity = async () => {
    if (!city.trim()) {
      setError('Please enter a valid city name.');
      return;
    }
    try {
      const response = await axios.get('/api/weather/', { params: { city } });
      setWeather(response.data);
      updateBg(response.data.weather[0]?.main);
      setError(null);
    } catch (err) {
      setError('Error fetching weather data for the city.');
    }
  };

  // Update background class based on weather condition
  const updateBg = (condition) => {
    if (!condition) return;
    const cond = condition.toLowerCase();
    if (cond.includes('rain')) {
      setBgClass('rainy');
    } else if (cond.includes('clear')) {
      setBgClass('sunny');
    } else if (cond.includes('cloud')) {
      setBgClass('cloudy');
    } else {
      setBgClass('');
    }
  };

  return (
    <div className={`container dynamic-bg ${bgClass}`}>
      <header className="header">
        <h1>Catchy Weather App</h1>
      </header>
      <div className="search-box">
        <input
          type="text"
          placeholder="Enter city name..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button onClick={fetchWeatherByCity}>Search</button>
      </div>
      {error && <div className="error">{error}</div>}
      {weather && (
        <div className="weather-info">
          <h2>{weather.name}</h2>
          <p className="temp">{weather.main.temp} Â°C</p>
          <p className="desc">{weather.weather[0]?.description}</p>
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
