import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const categories = ['general', 'business', 'entertainment', 'health', 'science', 'sports', 'technology'];
const BACKEND_URL = 'http://localhost:8000/api/news/';

function App() {
  const [news, setNews] = useState([]);
  const [category, setCategory] = useState('general');

  const fetchNews = useCallback(async () => {
    try {
      const response = await axios.get(BACKEND_URL, {
        params: { category }
      });
      // GNews API returns articles under the "articles" key.
      setNews(response.data.articles || []);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  }, [category]);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  return (
    <div className="app">
      {/* TOP HEADER */}
      <header className="top-header">
        <div className="logo">News App</div>
        <nav className="main-nav">
          {/* You could put additional nav links here if desired */}
          <ul>
            <li><a href="#!">World</a></li>
            <li><a href="#!">Politics</a></li>
            <li><a href="#!">Business</a></li>
            <li><a href="#!">Health</a></li>
            <li><a href="#!">Entertainment</a></li>
            <li><a href="#!">Travel</a></li>
            <li><a href="#!">Sports</a></li>
          </ul>
        </nav>
      </header>

      {/* SUB-NAVIGATION BAR */}
      <div className="sub-nav">
        <div className="trending-topics">
          <span>Trending:</span>
          <a href="#!">OpenAI</a>
          <a href="#!">Target boycott</a>
          <a href="#!">Emma Raducanu</a>
          <a href="#!">Tiny island 'golden'</a>
        </div>
        <div className="filter-container">
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            {categories.map((cat) => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>
      </div>

      {/* MAIN CONTENT */}
      <main className="main-content">
        <div className="left-column">
          {/* FEATURED ARTICLE (first item) */}
          {news && news.length > 0 && (
            <div className="featured-article">
              <img
                src={news[0].image || 'https://via.placeholder.com/600x400?text=No+Image'}
                alt={news[0].title}
              />
              <h2>{news[0].title}</h2>
              <p>
                {news[0].description
                  ? news[0].description.slice(0, 150) + '...'
                  : 'No description available.'}
              </p>
              <a href={news[0].url} target="_blank" rel="noopener noreferrer">
                Read More
              </a>
            </div>
          )}

          {/* OTHER ARTICLES (rest of the items) */}
          <div className="other-articles">
            {news && news.length > 1 ? (
              news.slice(1).map((article, index) => (
                <div key={index} className="news-card">
                  <img
                    src={article.image || 'https://via.placeholder.com/300x200?text=No+Image'}
                    alt={article.title}
                  />
                  <div className="news-content">
                    <h3>{article.title}</h3>
                    <p>
                      {article.description
                        ? article.description.slice(0, 100) + '...'
                        : 'No description available.'}
                    </p>
                    <a href={article.url} target="_blank" rel="noopener noreferrer">
                      Read More
                    </a>
                  </div>
                </div>
              ))
            ) : (
              <p className="no-news">No more news available.</p>
            )}
          </div>
        </div>

        {/* RIGHT COLUMN / SIDEBAR */}
        <aside className="right-column">
          <div className="breaking-news">
            <h3>Breaking News</h3>
            {/* For demonstration, just show a small list of recent headlines */}
            {news && news.length > 0 ? (
              <ul>
                {news.slice(0, 5).map((item, idx) => (
                  <li key={idx}>
                    <a href={item.url} target="_blank" rel="noopener noreferrer">
                      {item.title}
                    </a>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No breaking news available.</p>
            )}
          </div>
        </aside>
      </main>

      {/* FOOTER */}
      <footer className="footer">
        <p>Â© 2025 News App. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
