import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const BACKEND_URL = 'http://localhost:8000/api/news/';

function App() {
  // For demo purposes, we fetch news from GNews (or use static placeholders)
  const [news, setNews] = useState([]);
  const [trending] = useState([
    "Ukraine military aid",
    "Trump’s tariffs",
    "India avalanche",
    "More trending topics"
  ]);

  const fetchNews = useCallback(async () => {
    try {
      const response = await axios.get(BACKEND_URL, {
        params: { category: 'general' }
      });
      // GNews API returns articles under "articles"
      setNews(response.data.articles || []);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  }, []);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  // For the demo: use the first article as the featured story,
  // the second as the side column item, and the rest as additional news.
  const featuredArticle = news.length > 0 ? news[0] : null;
  const sideArticle = news.length > 1 ? news[1] : null;
  const additionalArticles = news.length > 2 ? news.slice(2) : [];

  return (
    <div className="App">
      {/* 1. Header Section */}
      <header className="header">
        <div className="logo">News App</div>
        <nav className="nav-bar">
          <a href="#us">US</a>
          <a href="#world">World</a>
          <a href="#politics">Politics</a>
          <a href="#business">Business</a>
          <a href="#health">Health</a>
          <a href="#entertainment">Entertainment</a>
          <a href="#style">Style</a>
          <a href="#travel">Travel</a>
          <a href="#sports">Sports</a>
          <a href="#more">More</a>
        </nav>
        <div className="ticker">
          {trending.map((item, index) => (
            <span key={index} className="ticker-item">
              {item}
            </span>
          ))}
        </div>
      </header>

      {/* 2. Main Headline & Featured Story */}
      <section className="featured-story">
        <div className="featured-image">
          {featuredArticle ? (
            <img
              src={featuredArticle.image || 'https://via.placeholder.com/800x400?text=Featured+Image'}
              alt={featuredArticle.title}
            />
          ) : (
            <img src="https://via.placeholder.com/800x400?text=Featured+Image" alt="Featured" />
          )}
        </div>
      </section>


      {/* 4. Additional News Items */}
      <section className="additional-news">
        {additionalArticles.length > 0 ? (
          additionalArticles.map((article, index) => (
            <div key={index} className="additional-card">
              <img
                src={article.image || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={article.title}
              />
              <h3>{article.title}</h3>
              <p>
                {article.description
                  ? article.description.slice(0, 80) + '...'
                  : 'No description available.'}
              </p>
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                Read More
              </a>
            </div>
          ))
        ) : (
          <p>No additional news available.</p>
        )}
      </section>

    </div>
  );
}

export default App;
