import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [news, setNews] = useState([]);
  const [city, setCity] = useState('');
  const [category, setCategory] = useState('');
  const [searchCity, setSearchCity] = useState('');

  // Get location on component mount (using geolocation with fallback)
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          // Here you might use a reverse-geocoding API to determine the actual city.
          // For simplicity, we use a placeholder city.
          setCity('New York');
        },
        () => setCity('New York')
      );
    } else {
      setCity('New York');
    }
  }, []);

  // Fetch news whenever the city or category changes
  useEffect(() => {
    if (city) {
      fetchNews(city, category);
    }
  }, [city, category]);

  const fetchNews = (cityName, categoryName) => {
    axios
      .get('/api/news/', {
        params: {
          city: cityName,
          category: categoryName,
        },
      })
      .then((response) => {
        setNews(response.data.articles || []);
      })
      .catch((error) => {
        console.error('Error fetching news:', error);
      });
  };

  const handleCitySearch = () => {
    if (searchCity.trim()) {
      setCity(searchCity);
      fetchNews(searchCity, category);
    }
  };

  return (
    <div className="App">
      <header className="header">
        <h1>Hot News App</h1>
        <p>Stay updated with the latest news from your area and beyond!</p>
      </header>

      <div className="search-container">
        <input
          type="text"
          placeholder="Enter city name..."
          value={searchCity}
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <button onClick={handleCitySearch}>Search</button>
      </div>

      <div className="filter-container">
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">All Categories</option>
          <option value="sports">Sports</option>
          <option value="entertainment">Entertainment</option>
          <option value="technology">Technology</option>
          <option value="business">Business</option>
        </select>
      </div>

      <div className="news-grid">
        {news.length > 0 ? (
          news.map((article, index) => (
            <div className="news-card" key={index}>
              <img
                src={article.image || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={article.title}
              />
              <div className="news-content">
                <h3>{article.title}</h3>
                <p>
                  {article.description
                    ? article.description.slice(0, 100) + '...'
                    : 'No description available.'}
                </p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">
                  Read More
                </a>
              </div>
            </div>
          ))
        ) : (
          <p className="no-news">No news available.</p>
        )}
      </div>
    </div>
  );
}

export default App;
