import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const categories = ['general', 'business', 'entertainment', 'health', 'science', 'sports', 'technology'];
const BACKEND_URL = 'http://localhost:8000/api/news/';

function App() {
  const [news, setNews] = useState([]);
  const [category, setCategory] = useState('general');

  const fetchNews = useCallback(async () => {
    try {
      const response = await axios.get(BACKEND_URL, {
        params: { category }
      });
      // Currents API returns articles under the "news" key.
      setNews(response.data.news || []);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  }, [category]);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  return (
    <div className="App">
      <header className="header">
        <h1>News App</h1>
        <p>Stay updated with the latest news based on category!</p>
      </header>

      <div className="filter-container">
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          {categories.map((cat) => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      <div className="news-grid">
        {news && news.length > 0 ? (
          news.map((article, index) => (
            <div key={index} className="news-card">
              <img
                src={article.image || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={article.title}
              />
              <div className="news-content">
                <h3>{article.title}</h3>
                <p>
                  {article.description
                    ? article.description.slice(0, 100) + '...'
                    : 'No description available.'}
                </p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">
                  Read More
                </a>
              </div>
            </div>
          ))
        ) : (
          <p className="no-news">No news available.</p>
        )}
      </div>
    </div>
  );
}

export default App;
