import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const BACKEND_URL = 'http://localhost:8000/api/news/';

function App() {
  // Fetch news from GNews (or use static placeholders)
  const [news, setNews] = useState([]);
  const [trending] = useState([
    "Ukraine military aid",
    "Trump’s tariffs",
    "India avalanche",
    "More trending topics"
  ]);

  const fetchNews = useCallback(async () => {
    try {
      const response = await axios.get(BACKEND_URL, {
        params: { category: 'general' }
      });
      // GNews API returns articles under "articles"
      setNews(response.data.articles || []);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  }, []);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  // Use the first article as the featured story,
  // the second as the side column item, and the rest as additional news.
  const featuredArticle = news.length > 0 ? news[0] : null;
  const sideArticle = news.length > 1 ? news[1] : null;
  const additionalArticles = news.length > 2 ? news.slice(2) : [];

  return (
    <div className="App">
      {/* 1. Header Section */}
      <header className="header">
        <div className="logo">News App</div>
        <nav className="nav-bar">
          <a href="#general">GENERAL</a>
        </nav>
        <div className="ticker">
          {trending.map((item, index) => (
            <span key={index} className="ticker-item">{item}</span>
          ))}
        </div>
      </header>

      {/* 2. Main Headline & Featured Story */}
      <section className="featured-story">
        <div className="featured-image">
          {featuredArticle ? (
            <img
              src={featuredArticle.image || 'https://via.placeholder.com/800x400?text=Featured+Image'}
              alt={featuredArticle.title}
            />
          ) : (
            <img
              src="https://via.placeholder.com/800x400?text=Featured+Image"
              alt="Featured"
            />
          )}
        </div>
        <div className="featured-content">
          <h1>{featuredArticle ? featuredArticle.title : "Trump digs in on divisive agenda"}</h1>
          <p>
            {featuredArticle
              ? featuredArticle.description || "Detailed context about the featured story goes here."
              : "Detailed context about the featured story goes here."}
          </p>
        </div>
      </section>

      {/* 3. Side Column (Secondary News) */}
      <section className="side-column">
        <h2>
          China defies Trump’s trade war by ambitious 5% growth target, vows over any difficulty.
        </h2>
        <ul className="side-list">
          {sideArticle ? (
            <li>{sideArticle.title}</li>
          ) : (
            <>
              <li>Related point 1</li>
              <li>Related point 2</li>
              <li>Related point 3</li>
            </>
          )}
        </ul>
      </section>

      {/* 4. Additional News Items */}
      <section className="additional-news">
        {additionalArticles.length > 0 ? (
          additionalArticles.map((article, index) => (
            <div key={index} className="additional-card">
              <img
                src={article.image || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={article.title}
              />
              <h3>{article.title}</h3>
              <p>
                {article.description
                  ? article.description.slice(0, 80) + '...'
                  : 'No description available.'}
              </p>
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                Read More
              </a>
            </div>
          ))
        ) : (
          <p>No additional news available.</p>
        )}
      </section>

      {/* 5. Footer (Taskbar) */}
      <footer className="footer">
        <div className="weather">71°F, Mostly sunny</div>
        <div className="url-bar">
          <a href="#emma">Emma Raducanu - Sports Story</a>
        </div>
        <div className="taskbar">
          <span className="taskbar-item">Start</span>
          <span className="taskbar-item">App1</span>
          <span className="taskbar-item">App2</span>
          <span className="taskbar-item">App3</span>
        </div>
      </footer>
    </div>
  );
}

export default App;
