import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

const categories = [
  'general',
  'business',
  'entertainment',
  'health',
  'science',
  'sports',
  'technology',
];

const BACKEND_URL = 'http://localhost:8000/api/news/';

function App() {
  const [news, setNews] = useState([]);
  const [category, setCategory] = useState('general');

  const fetchNews = useCallback(async () => {
    try {
      const response = await axios.get(BACKEND_URL, {
        params: { category },
      });
      // Currents API returns articles under the "news" key
      setNews(response.data.news || []);
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  }, [category]);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  // Let's consider the first article as the "main" article
  const mainArticle = news.length > 0 ? news[0] : null;
  // The rest go into the sidebar
  const sideArticles = news.length > 1 ? news.slice(1) : [];

  return (
    <div className="App">
      {/* Header Section */}
      <header className="header">
        <div className="header-left">
          <h1>News App</h1>
          <nav className="navbar">
            {categories.map((cat) => (
              <a
                key={cat}
                href="#!"
                onClick={(e) => {
                  e.preventDefault();
                  setCategory(cat);
                }}
                style={{
                  color: category === cat ? '#cc0000' : '#333',
                }}
              >
                {cat.toUpperCase()}
              </a>
            ))}
          </nav>
        </div>
      </header>

      {/* Main Content Area */}
      <div className="main-content">
        {/* Main Article */}
        <section className="main-article">
          {mainArticle ? (
            <>
              <img
                src={
                  mainArticle.image ||
                  'https://via.placeholder.com/600x400?text=No+Image'
                }
                alt={mainArticle.title}
              />
              <h2>{mainArticle.title}</h2>
              <p>
                {mainArticle.description
                  ? mainArticle.description
                  : 'No description available.'}
              </p>
              <a
                href={mainArticle.url}
                target="_blank"
                rel="noopener noreferrer"
              >
                Read More
              </a>
            </>
          ) : (
            <p>No main article available.</p>
          )}
        </section>

        {/* Sidebar */}
        <aside className="side-bar">
          {sideArticles.map((article, index) => (
            <div key={index} className="side-bar-article">
              <img
                src={
                  article.image ||
                  'https://via.placeholder.com/300x200?text=No+Image'
                }
                alt={article.title}
              />
              <h3>{article.title}</h3>
              <p>
                {article.description
                  ? article.description.slice(0, 80) + '...'
                  : 'No description available.'}
              </p>
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                Read More
              </a>
            </div>
          ))}
        </aside>
      </div>

      {/* Footer */}
      <footer className="footer">
        &copy; {new Date().getFullYear()} News App. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
